        </View>
        <View style={{height:'90%',width:'50%'}}>
          <Text style={{ fontSize:20, fontWeight: '500', color: '#68B0AB',marginLeft:'2%' }}>处方信息</Text>
          <Text style={{ fontSize:16, fontWeight: '500',marginTop:'5%' ,marginLeft:'2%'}}>{I18n.t('preNumber')}: 01{new Date().getFullYear().toString()}{new Date().getMonth().toString()}{new Date().getDate().toString()}</Text>
          <ScrollView
                  style={{flex:1,width:'100%',maxHeight:'40%',marginLeft:'2%'}}
                  >
          {this.state.medicine.length>0 ? medicine
          :
            <View style={{alignItems:'center',justifyContent:'center',width:'90%',height:100,borderRadius:10,backgroundColor:'#EBEBEB',margin:'5%'}}>
              <Text style={{fontSize: 16, fontWeight: '400'}}>暂无药品，请添加</Text>
            </View>
