import React ,{Component}from 'react';
import { KeyboardAvoidingView,Text,  View, Alert, Image,TouchableOpacity,ScrollView,SafeAreaView,TextInput,Platform,ActivityIndicator } from 'react-native';
import { MaterialCommunityIcons,  FontAwesome} from '@expo/vector-icons';
import DataContext from '../../providerContext';
import I18n from '../switchLanguage';
import * as Permissions from "expo-permissions";
import ImageView from 'react-native-image-view';

export default class Pathology extends Component {
    constructor(props) {
      super(props);
      this.state={
      complaint:'',
      doctorComment:'',
      patientComment:'',
      id:null,
      talk:false,
      hasAudioPermission: null,
      image:[],
      visible:false,
      name:'',
      gender:'',
      dob:'',
      mobile:'',
      medicareCard:[],
      language:[],
      userId:null,
      infoId:null,
      medicineUsage:[],
      familyHistory:[],
      chronic:[],
      allergy:[],
      medicine:[],
      }
    }

  async componentDidMount(){
//      this.setState({id:this.props.route.params.id});
//      if(this.props.route.params.medicine != null) {
//        this.setState({medicine:this.props.route.params.medicine});
//      }
//      this.queryPatient();
  }

  queryPatient() {
    let url = 'http://'
    +this.context.url
    +'/aicare-business-api/business/medical-report/query-medical-info-by-appointment'
    +'?customerAppointmentId='+this.props.route.params.id ;
    fetch(url,{
      method: 'GET',
      mode: 'cors',
      credentials: 'include',
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      'sso-auth-token': this.context.token,
      'sso-refresh-token': this.context.refresh_token,
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Credentials': true,
      'Access-Control-Allow-Headers': 'content-type, sso-auth-token',
      'Access-Control-Allow-Methods': 'GET,POST,OPTIONS,DELETE',
    }})
    .then((response) => response.json())
    .then((json) => {
      if (json.code === 0) {
        this.setState({
          name:json.medicalInfo.firstName + json.medicalInfo.lastName,
          gender:json.medicalInfo.gender,
          dob:json.medicalInfo.dob,
          mobile:json.medicalInfo.mobile,
          medicareCard:json.medicalInfo.medicareCard,
          language:json.medicalInfo.languages,
          userId:json.medicalInfo.customerUserId,
          infoId:json.medicalInfo.customerUserInfoId,
          patientComment:json.medicalInfo.patientStatement,
          allergy:json.medicalInfo.allergen,
          medicineUsage:json.medicalInfo.medicineUsage,
          familyHistory:json.medicalInfo.familyHistory,
          chronic:json.medicalInfo.chronic,
        })
       let src = {uri: json.medicalInfo.commentImg,}
       let image = []
       let obj = {source:src}
       image.push(obj)
       this.setState({
          image:image
        })
      } else {
        console.log(json.msg)
      }
    })
    .catch(error => console.warn(error));
  }



  render() {
    console.log(this.state.image)
    if (this.state.isLoading){
      return(
     <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
         <ActivityIndicator size="large" color="#00ff00"  />
      </View>
    )
    }else {
    return (
    <View style={{ flex:1, flexDirection:'row',justifyContent: "center", alignItems: "center" ,backgroundColor:'rgb(51,51,51)'}}>
      <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "height"} style={{flex:1,height:'95%',width:'90%',backgroundColor:'white',borderRadius:5,padding:'2%'}}>
        <ScrollView style={{flex:1}}>
          <View style={{flex:1,width:'90%',flexDirection:'row',height:300,justifyContent:'space-between'}}>
            <View style={{width:'47.5%',height:'100%',borderWidth:2,borderColor:'rgb(33,192,196)'}}>
              <View style={{width:'100%',height:'10%',backgroundColor:'rgb(33,192,196)'}}>
                <Text style={{color:'white',marginLeft:'5%'}}>{I18n.t('pInfo')}</Text>
              </View>
              <View style={{width:'100%',height:'90%'}}>
              </View>
            </View>
            <View style={{width:'47.5%',height:'100%',borderWidth:2,borderColor:'rgb(33,192,196)'}}>
              <View style={{width:'100%',height:'10%',backgroundColor:'rgb(33,192,196)'}}>
                <Text style={{color:'white',marginLeft:'5%'}}>{I18n.t('pInfo')}</Text>
              </View>
              <View style={{width:'100%',height:'90%'}}>
              </View>
            </View>
          </View>
        </ScrollView>
      </KeyboardAvoidingView>
    </View>
  );}}
}
Pathology.contextType = DataContext;
