                    <Text>{moment(this.state.times[5].time2).format('LT')} </Text>
                  </TouchableOpacity>
                </View>
              }
              <DateTimePicker
                isVisible={this.state.times[5].visible1}
                onConfirm={(time) => {
                    let t = this.state.times;
                    t[5].time1 = time;
                    t[5].visible1 = false;
                  this.setState({times:t})}}
                onCancel={()=> {
                    let t = this.state.times;
                    t[5].visible1 = false;
                  this.setState({times:t})}}
                mode={'time'}
                display="spinner"
                value={this.state.times[5].time1}
                minuteInterval={10}
                />
                <DateTimePicker
                  isVisible={this.state.times[5].visible2}
                  onConfirm={(time) => {
                      let t = this.state.times;
                      t[5].time2 = time;
                      t[5].visible2 = false;
                    this.setState({times:t})}}
                  onCancel={()=> {
                      let t = this.state.times;
                      t[5].visible2 = false;
                    this.setState({times:t})}}
                  mode={'time'}
                  display="spinner"
                  value={this.state.times[5].time2}
                  minuteInterval={10}
                  />
              </View>
              <View style={{flexDirection: 'row'}}>
              <TouchableOpacity style={{
                backgroundColor:this.state.buttons[6].backgroundColor,
                borderWidth: this.state.buttons[6].borderWidth,
                width: 60,
                height: 30,
                borderRadius: 10,
                marginTop: 5,
                marginBottom: 5,
                marginRight: 25,
                alignItems: 'center',
                justifyContent: 'center',
                }}
                onPress={()=>this.changeColor(6)}>
                <Text style={{ fontSize:16, fontWeight: '400', color: this.state.buttons[6].fontColor }}>周天</Text>
              </TouchableOpacity>
              { this.state.buttons[6].pressed &&
                <View style={{flexDirection: 'row'}}>
                <TouchableOpacity style={styles.timePick} onPress={()=>{
                  let t = this.state.times;
                  t[6].visible1 = true;
                  this.setState({times:t})}}>
                  <Text>{moment(this.state.times[6].time1).format('LT')}</Text>
                </TouchableOpacity>
                  <Text> _ </Text>
                  <TouchableOpacity style={styles.timePick} onPress={()=>{
                    let t = this.state.times;
                    t[6].visible2 = true;
                    this.setState({times:t})}}>
                    <Text>{moment(this.state.times[6].time2).format('LT')} </Text>
                  </TouchableOpacity>
                </View>
              }
              <DateTimePicker
                isVisible={this.state.times[6].visible1}
                onConfirm={(time) => {
                    let t = this.state.times;
                    t[6].time1 =time;
                    t[6].visible1 = false;
                  this.setState({times:t})}}
                onCancel={()=> {
                    let t = this.state.times;
                    t[6].visible1 = false;
                  this.setState({times:t})}}
                mode={'time'}
                display="spinner"
                value={this.state.times[6].time1}
                minuteInterval={10}
                />
                <DateTimePicker
                  isVisible={this.state.times[6].visible2}
                  onConfirm={(time) => {
                      let t = this.state.times;
                      t[6].time2 = time;
                      t[6].visible2 = false;
                    this.setState({times:t})}}
                  onCancel={()=> {
                      let t = this.state.times;
                      t[6].visible2 = false;
                    this.setState({times:t})}}
                  mode={'time'}
                  display="spinner"
                  value={this.state.times[6].time2}
                  minuteInterval={10}
                  />
              </View>
            <View style={{ marginTop:10, marginBottom:10}}>
              <Text style={{ fontSize:16, fontWeight: '400' }}>服务类型</Text>
            </View>
            <View style={{flexDirection: 'row'}}>
              <CheckBox
                center
                title='实地问诊                                '
                iconRight
                checkedIcon='check-circle-o'
                uncheckedIcon='circle-o'
                checkedColor='red'
                containerStyle={{borderWidth:0,backgroundColor:'white'}}
                size={this.state.size}
                checked={this.state.service[0].status==1?true:false}
                onPress={() => {
                  let t = this.state.service;
                  t[0].status = !this.state.service[0].status;
                  this.setState({service:t})}}
               />
            </View>
            <View style={{flexDirection: 'row'}}>
              <CheckBox
                center
                title='远程问诊                                '
                iconRight
                checkedIcon='check-circle-o'
                uncheckedIcon='circle-o'
                containerStyle={{borderWidth:0, backgroundColor:'white'}}
                checkedColor='red'
                size={this.state.size}
                checked={this.state.service[1].status==1?true:false}
                onPress={() => {
                  let t = this.state.service;
                  t[1].status = !this.state.service[1].status;
                  this.setState({service:t})}}
               />
            </View>
            <View style={{ marginTop:10, marginBottom:10}}>
              <Text style={{ fontSize:16, fontWeight: '400' }}>收费方式</Text>
            </View>
            <View style={{flexDirection: 'row'}}>
              <CheckBox
                center
                title='bulk billing                          '
                iconRight
                checkedIcon='check-circle-o'
                uncheckedIcon='circle-o'
                containerStyle={{borderWidth:0,backgroundColor:'white'}}
                checkedColor='red'
                size={this.state.size}
                checked={this.state.checked8}
                onPress={() => {
                  this.setState({
                  checked8: !this.state.checked8
                })}}
               />
            </View>

        <TouchableOpacity style={styles.resumeButton} onPress={() => {this.sendRequest()}}>
          <Text style={{ fontSize:16, fontWeight: '400', color: '#ffffff' }}>确认</Text>
        </TouchableOpacity>
        </View>
      </ScrollView>
    </SafeAreaView>
  );}
}
UploadMember.contextType = DataContext;
