import React ,{Component}from 'react';
import { Text, View, Image,SafeAreaView,ScrollView,TouchableOpacity } from 'react-native';
import { SearchBar } from 'react-native-elements';
import {styles} from '../providerStyle';
import {data} from './data';
import moment from "moment"

export default class ProcessingOrder extends Component {
    constructor(props) {
      super(props);
      date: new Date();
      this.state={
        eventDate:moment.duration().add({days:9,hours:3,minutes:40,seconds:50}), // add 9 full days, 3 hours, 40 minutes and 50 seconds
        days:0,
        hours:0,
        mins:0,
        secs:0,
        buttons: [
          { backgroundColor: '#68B0AB', pressed: false,id:0},
          { backgroundColor: '#68B0AB', pressed: false,id:1},
          { backgroundColor: '#68B0AB', pressed: false,id:2},
          { backgroundColor: '#68B0AB', pressed: false,id:3},
          { backgroundColor: '#68B0AB', pressed: false,id:4},
          { backgroundColor: '#68B0AB', pressed: false,id:5},
          { backgroundColor: '#68B0AB', pressed: false,id:6},
          { backgroundColor: '#68B0AB', pressed: false,id:7},
          { backgroundColor: '#68B0AB', pressed: false,id:8},
          { backgroundColor: '#68B0AB', pressed: false,id:9},
        ]
      };
    }


  componentDidMount = () => {
    console.log("set buttons work")
    var i=0;
    let butt=[];
    while (i < data.length) {
      butt.push({ backgroundColor: '#68B0AB', pressed: false,id:i});
      i++;
    }
    this.setState({buttons:butt});
    this.updateTimer();
  }

  updateTimer=()=>{
   const x = setInterval(()=>{
     let { eventDate} = this.state

     if(eventDate <=0){
       clearInterval(x)
     }else {
       eventDate = eventDate.subtract(1,"s")
       const days = eventDate.days()
       const hours = eventDate.hours()
       const mins = eventDate.minutes()
       const secs = eventDate.seconds()
       this.setState({
         days,
         hours,
         mins,
         secs,
         eventDate
       })
     }
   },1000)

 }

  changeColor(index){
    let but = this.state.buttons;
    if(!but[index].pressed){
       but[index].pressed = true;
       but[index].backgroundColor = '#FF7E67';
       this.setState({buttons: but});
    } else {
      but[index].pressed = false;
      but[index].backgroundColor = '#68B0AB';
      this.setState({buttons: but});
    }
  }

  render () {
    console.log (this.state);
    if (data.length >0) {
    const orders = data.map((item) => {
      return (
        <View style={styles.card} key={item.id}>
          <View style={{flexDirection: 'row', marginTop:16, marginBottom:16, marginLeft:25}}>
          <Image
            style = {styles.pendingImg}
            source = {require('../../images/providerImg/home_img_person.png')}
          />
          <View>
            <Text style={{fontSize:16, color:'#333333', fontWeight: '500'}}>{item.name}</Text>
            <Text style={{fontSize:12, color:'#666666', fontWeight: '400'}}>{item.address}</Text>
          </View>
          </View>
          <View style={{flexDirection: 'row',paddingBottom: 15, borderBottomWidth: 1, borderBottomColor:'#EEEEEE'}}>
            <Image
              style = {{width: 15, height:15 , marginLeft:25, marginRight:5}}
              source = {require('../../images/providerImg/order_icon_phone.png')}
            />
            <Text style={{fontSize:12, color:'#999999', fontWeight: '400'}}>{item.phone}</Text>
            <Image
              style = {{width: 15, height:15,marginLeft:130, marginRight:5}}
              source = {require('../../images/providerImg/order_iocn_money.png')}
            />
            <Text style={{fontSize:12, color:'#999999', fontWeight: '400'}}>{item.price}</Text>
          </View>
          <View style={{flexDirection: 'row-reverse'}}>
            <TouchableOpacity style={{
              width: 75,
              height: 30,
              backgroundColor: this.state.buttons[item.id].backgroundColor,
              borderRadius: 10,
              textAlign: 'center',
              marginRight: 25,
              marginTop: 15,
            }} onPress={()=>this.changeColor(item.id)}>
              <Text style={{fontSize:14, color:'#FAFAFA'}}>开始</Text>
            </TouchableOpacity>
          </View>
        </View>
      )
    })
    return (
      <SafeAreaView style={{ flex:1, justifyContent: "center", alignItems: "center" }}>
        <Text style={{fontSize:20}}>{`${this.state.days} : ${this.state.hours} : ${this.state.mins} : ${this.state.secs}`}</Text>
        <ScrollView style={{ flex:1}}>
          {orders}
        </ScrollView>
      </SafeAreaView>
    )} else {
    return (
      <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <Image
        style = {styles.finishImg}
        source = {require('../../images/providerImg/order_img_empty_inprogress1.png')}
      />
     <Text style={{ color: '#333333', fontSize: 16, fontWeight: '400'}}>您还没有新订单哦，快去接取吧！</Text>
     </View>
    )
  }
  }
}
