import React ,{Component}from 'react';
import { Text, View, Image,SafeAreaView,TouchableOpacity,ActivityIndicator } from 'react-native';
import DataContext from '../../providerContext';
import I18n from '../switchLanguage';

export default class MyAccount extends Component {
    constructor(props) {
      super(props);
      this.state={
        balance:100,
        week:30,
        month:50,
        total:100,
      };
    }
    componentDidMount(){
      this.query();
      this.queryList();
    }
    query() {
      this.setState({isLoading:true})
      let url = 'http://'
      +this.context.url
      +'/aicare-business-api/business/acount/info'
        fetch(url,{
          method: 'GET',
          mode: 'cors',
          credentials: 'include',
          headers: {
          'Accept':       'application/json',
          'Content-Type': 'application/json',
          'sso-auth-token': this.context.token,
          'sso-refresh-token': this.context.refresh_token,
        }})
        .then((response) => response.json())
        .then((json) => {
          this.setState({isLoading:false})
          if (json.code === 0) {
            console.log(json);
            this.setState({
              week:json.data.weekAcount ,
              month:json.data.monthAcount,
              balance:json.data.balanceAcount,
              total:json.data.totalAcount,
            });

          } else {
            console.log(json.msg)
          }
        }).catch(error => console.warn(error));
    }
    queryList() {
      this.setState({isLoading:true})
      let url = 'http://'
      +this.context.url
      +'/aicare-business-api/business/acount/list'
        fetch(url,{
          method: 'GET',
          mode: 'cors',
          credentials: 'include',
          headers: {
          'Accept':       'application/json',
          'Content-Type': 'application/json',
          'sso-auth-token': this.context.token,
          'sso-refresh-token': this.context.refresh_token,
        }})
        .then((response) => response.json())
        .then((json) => {
          this.setState({isLoading:false})
          if (json.code === 0) {
            console.log(json);
            this.setState({list:json.page.list});
          } else {
            console.log(json.msg)
          }
        }).catch(error => console.warn(error));
    }
  render () {
    var date = new Date().getDate();
    var month = new Date().getMonth() + 1;
    if (this.state.isLoading){
      return(
      <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        <ActivityIndicator size="large" color="#00ff00"  />
     </View>
    )
    }else {
    return (
      <SafeAreaView style={{ width:'100%',flex:1,backgroundColor:"white",alignItems: 'center', backgroundColor:'rgb(51,51,51)' }}>
        <View style={{width:'90%',height:'100%',alignItems: 'center', backgroundColor:'white'}}>
        <View style={{width:'95%',height:'30%',flexDirection: 'row',justifyContent:'center',alignItems: 'center'}}>
          <TouchableOpacity style={{height:90,width:'22%',marginLeft:'1%',marginRight:'1%',backgroundColor: '#9ADAD6',
          borderRadius: 15,justifyContent: "center",alignItems: "center"}}>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>{this.state.balance?this.state.balance:0}</Text>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>Balance</Text>
          </TouchableOpacity>
          <TouchableOpacity style={{height:90,width:'22%',marginLeft:'1%',marginRight:'1%',backgroundColor: '#196F75',
          borderRadius: 15,justifyContent: "center",alignItems: "center"}}>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>{this.state.week?this.state.week:0}</Text>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>Weekly Income</Text>
          </TouchableOpacity>
          <TouchableOpacity style={{height:90,width:'22%',marginLeft:'1%',marginRight:'1%',backgroundColor: '#FF816B',
          borderRadius: 15,justifyContent: "center",alignItems: "center"}}>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>{this.state.month?this.state.month:0}</Text>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>Monthly Income</Text>
          </TouchableOpacity>
          <TouchableOpacity style={{height:90,width:'22%',marginLeft:'1%',marginRight:'1%',backgroundColor: '#00FFFF',
          borderRadius: 15,justifyContent: "center",alignItems: "center"}}>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>{this.state.total?this.state.total:0}</Text>
            <Text style={{ color: '#FFFFFF', fontSize: 14, fontWeight: '600'}}>Total Income</Text>
          </TouchableOpacity>
        </View>
        <View style={{width:'90%',flexDirection: 'row',borderBottomWidth:1}}>
          <TouchableOpacity style={{marginRight:'5%',flexDirection: 'row',}}>
            <Text style={{fontSize:16,fontWeight: '600'}}>{month}/{date}</Text>
            <Image
              style = {{width: 16, height:16,marginLeft:5}}
              source = {require('../../images/providerImg/schedule_icon_calender.png')}
              />
          </TouchableOpacity>
        </View>

        <View style={{}}>
        </View>
       </View>
      </SafeAreaView>
    )
  }}
}
MyAccount.contextType = DataContext;
