import React ,{Component}from 'react';
import { Platform,KeyboardAvoidingView,Alert,Text, Button, View, Switch, Image,TouchableOpacity,ScrollView,SafeAreaView,TextInput } from 'react-native';
import {styles} from './providerStyle';
import { MaterialCommunityIcons, MaterialIcons, Ionicons } from '@expo/vector-icons';
import { CheckBox } from 'react-native-elements';
import DataContext from "../providerContext";
import I18n from './switchLanguage';

export default class Forget extends Component {
  state = {
    info:"",
    password:"",
    confirm: "",
    code:"",
    mobile:true,
    email:false,
    counter:60,
    timer:null,
  }
  sendRequest() {
    let s = this.state;
    if (this.state.confirm != this.state.password) {
      Alert.alert("两次密码必须相同")
    } else {
      if (this.state.email) {
    let url = 'http://'
    +this.context.url
    +'/aicare-business-api/business/user/updatepassword?'
    +'email='+ s.info
    +'&code=' + s.code
    +'&type=email'
    +'&appType=1'
    +'&password=' + s.password;
    console.log(url);
    fetch(url,{
      method: 'POST',
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      }
    })
    .then((response) => response.json())
    .then((json) =>  {
        if (json.code === 0) {
          Alert.alert("success");
          this.props.navigation.navigate('登陆');
        } else {
          Alert.alert(json.msg);
          return false;
        }
    })} else {
      let url = 'http://'
      +this.context.url
      +'/aicare-business-api/business/user/updatepassword?'
      +'mobile='+ s.info
      +'&code=' + s.code
      +'&type=mobile'
      +'&appType=1'
      +'&password=' + s.password;
      console.log(url);
      fetch(url,{
        method: 'POST',
        headers: {
        'Accept':       'application/json',
        'Content-Type': 'application/json',
        }
      })
      .then((response) => response.json())
      .then((json) =>  {
          if (json.code === 0) {
            Alert.alert("success");
            this.props.navigation.navigate('登陆');
          } else {
            Alert.alert(json.msg);
            return false;
          }
      })
    }}
  }
  sendCode() {
    this.setTimer();
    if (this.state.email) {
    let m = this.state.info;
    let url = 'http://'
    +this.context.url
    +'/aicare-business-api/business/user/send?'
    +'email=' + m
    +'&type=email';
    console.log(url);
    fetch(url,{
      method: 'POST',
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      }
    })
    .then((response) => response.json())
    .then((json) => {
      console.log(json.msg);
    });} else {
      let m = this.state.info;
      let url = 'http://'
      +this.context.url
      +'/aicare-business-api/business/user/send?'
      +'mobile=' + m
      +'&type=mobile';
      console.log(url);
      fetch(url,{
        method: 'POST',
        headers: {
        'Accept':       'application/json',
        'Content-Type': 'application/json',
        }
      })
      .then((response) => response.json())
      .then((json) => {
        console.log(json.msg);
      });
    }
  }

  componentWillUnmount() {
    clearInterval(this.state.timer);
  }

  setTimer() {
    let timer = setInterval(this.tick.bind(this), 1000);
    this.setState({timer});
  }

  tick() {
    if (this.state.counter == 0) {
      clearInterval(this.state.timer);
      this.setState({
        counter: 60,
        timer:null
      });
    }else {
      this.setState({
        counter: this.state.counter - 1
      });
    }
  }

  render() {
    return (
      <KeyboardAvoidingView
        style={{ flex:1 ,justifyContent: "center",backgroundColor:'white'}}
        behavior={Platform.OS === "ios" ? "padding" : "height"}
      >
      <View style={{width:'90%',flex:1, marginLeft:'5%',marginTop:35}}>
          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/signup_icon_link.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>{I18n.t('binding')}</Text>
          </View>

          <View style={{flexDirection: 'row'}}>
            <CheckBox
              center
              title={I18n.t('mobile')}
              iconRight
              checkedIcon='check-circle-o'
              uncheckedIcon='circle-o'
              checkedColor='red'
              containerStyle={{borderWidth:0, backgroundColor:'white'}}
              checked={this.state.mobile}
              onPress={() => {
                this.setState({
                mobile: true,
                email: false,
              })}}
             />
          </View>
          <View style={{flexDirection: 'row'}}>
            <CheckBox
              center
              title={I18n.t('email')}
              iconRight
              checkedIcon='check-circle-o'
              uncheckedIcon='circle-o'
              checkedColor='red'
              containerStyle={{borderWidth:0, backgroundColor:'white'}}
              checked={this.state.email}
              onPress={() => {
                this.setState({
                  mobile: false,
                  email: true,
              })}}
             />
          </View>

          <View style={{height:30,marginBottom:10}}>
            <TextInput style={styles.resumeInput}
            placeholder= {I18n.t('multipleInput')}
            onChangeText={(text) => {this.setState({ info: text})}}
            />
          </View>

          <View style={{flexDirection: 'row'}}>
            <TextInput style={styles.resumeInput3}
            placeholder={I18n.t('verifyInput')}
            onChangeText={(text) => {this.setState({ code: text})}}
            />
            {this.state.timer ?
            <TouchableOpacity style={styles.codeTab2}>
            <Text style={{ fontSize:14, fontWeight: '300',color:'white' }}>{I18n.t('resend')} {this.state.counter}s</Text>
          </TouchableOpacity>
            :
            <TouchableOpacity style={styles.codeTab} onPress={()=>this.sendCode()}>
            <Text style={{ color:'white',fontSize:14, fontWeight: '300' }}>{I18n.t('sendCode')}</Text>
          </TouchableOpacity>
            }
          </View>

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/login_icon_pswd.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>{I18n.t('password')}</Text>
          </View>
          <View>
          <TextInput style={{
            width: 300,
            height: 22,
            marginLeft: 5,
            borderBottomWidth:1,
            borderBottomColor:'#EEEEEE',}}
          placeholder={I18n.t('passwordInput')}
          onChangeText={(text) => {this.setState({ password: text})}}
          />
          </View>
          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/account_icon_confirm.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>{I18n.t('confirm')}</Text>
          </View>
          <View>
          <TextInput style={styles.resumeInput}
          placeholder={I18n.t('passwordInput2')}
          onChangeText={(text) => {this.setState({ confirm: text})}}
          />
          </View>
          <TouchableOpacity style={styles.resumeButton} onPress={()=>this.sendRequest()}>
            <Text style={{ fontSize:16, fontWeight: '400', color: '#ffffff' }}>{I18n.t('confirm')}</Text>
          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
  );}
}
Forget.contextType = DataContext;
