import React ,{Component}from 'react';
import { Text, Button, View, Switch, Image,TouchableOpacity,ScrollView,SafeAreaView,TextInput } from 'react-native';
import {styles} from './providerStyle';
import { MaterialCommunityIcons, MaterialIcons, Ionicons } from '@expo/vector-icons';
import { CheckBox } from 'react-native-elements';

export default class Signup extends Component {
  state = {
    name:"",
    phone:"",
    mail:"",
    password:"",
    confirm: "",
    userCode:"",
    mailCode:"",
    checked1: false,
    checked2: true,
    checked3: false,
    checked4: true,
    checked5: false,
  }
  sendRequest() {
    let s = this.state;

    let url = 'http://13.239.57.130:8080/aicare-register/register/provider?'
    +'name='+ s.name
    +'&phone=' + s.phone
    +'&email=' + s.mail
    +'&password=' + s.password;
    fetch(url,{
      method: 'POST',
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      }
    })
    .then((response) => console.log(response.status))
  //  .then(json => {console.log(json)});
  }
  sendCode() {
    let m = this.state.mail;
    let url = 'http://13.239.57.130:8080/aicare-smtp/smtp?'
    +'&mail-address=' + m
    console.log(url);
    fetch(url,{
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      }
    })
    .then((response) => response.json())
    .then((json) => {
      this.setState({ mailCode: json.code});
      console.log(json.code);
    });
  }

  render() {
    return (
      <SafeAreaView style={{ flex:1, justifyContent: "center", alignItems: "center" }}>
          <View style={{alignItems: "Left" }}>
          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/singup_icon_name.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>姓名</Text>
          </View>
          <View style={{borderBottomWidth:1, borderBottomColor:'#BBBBBB'}}>
          <TextInput style={styles.resumeInput}
          placeholder="请输入您的名字"
          onChangeText={(text) => {this.setState({ name: text})}}
          />
          </View>

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/login_icon_pswd.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>密码</Text>
          </View>
          <View style={{borderBottomWidth:1, borderBottomColor:'#BBBBBB'}}>
          <TextInput style={styles.resumeInput}
          placeholder="请输入您的密码"
          onChangeText={(text) => {this.setState({ password: text})}}
          />
          </View>

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/account_icon_confirm.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>确认</Text>
          </View>
          <View style={{borderBottomWidth:1, borderBottomColor:'#BBBBBB'}}>
          <TextInput style={styles.resumeInput}
          placeholder="请再次输入您的密码"
          onChangeText={(text) => {this.setState({ confirm: text})}}
          />
          </View>

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/signup_icon_service.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>服务</Text>
          </View>
          <CheckBox
            center
            title='上门服务'
            iconRight
            checkedIcon='check-circle-o'
            uncheckedIcon='circle-o'
            checkedColor='red'
            size={this.state.size}
            checked={this.state.checked1}
            onPress={() => this.setState({
              checked1: true,
              checked2: false,
              checked3: false,
            })}
           />
           <CheckBox
             center
             title='远程医疗'
             iconRight
             checkedIcon='check-circle-o'
             uncheckedIcon='circle-o'
             checkedColor='red'
             size={this.state.size}
             checked={this.state.checked2}
             onPress={() => this.setState({
               checked1: false,
               checked2: true,
               checked3: false,
             })}
            />
            <CheckBox
              center
              title='药品配送'
              iconRight
              checkedIcon='check-circle-o'
              uncheckedIcon='circle-o'
              checkedColor='red'
              size={this.state.size}
              checked={this.state.checked3}
              onPress={() => this.setState({
                checked1: false,
                checked2: false,
                checked3: true,
              })}
             />
             <Text>医疗角色</Text>
             <CheckBox
               center
               title='医疗机构'
               iconRight
               checkedIcon='check-circle-o'
               uncheckedIcon='circle-o'
               checkedColor='red'
               size={this.state.size}
               checked={this.state.checked4}
               onPress={() => this.setState({
                 checked1: true,
                 checked2: false,
               })}
              />
              <CheckBox
                center
                title='个人医生          '
                iconRight
                checkedIcon='check-circle-o'
                uncheckedIcon='circle-o'
                checkedColor='red'
                size={this.state.size}
                checked={this.state.checked5}
                onPress={() => this.setState({
                  checked1: false,
                  checked2: true,
                })}
               />
          </View>
          <TouchableOpacity style={styles.resumeButton} onPress={()=>this.sendRequest()}>
            <Text style={{ fontSize:16, fontWeight: '400', color: '#ffffff' }}>确认</Text>
          </TouchableOpacity>
      </SafeAreaView>
  );}
}
