import React ,{Component}from 'react';
import { Text, Button, View, Switch, Image,TouchableOpacity,ScrollView,SafeAreaView,TextInput } from 'react-native';
import {styles} from './providerStyle';
import { MaterialCommunityIcons, MaterialIcons, Ionicons } from '@expo/vector-icons';
import { CheckBox } from 'react-native-elements'

export default class Verify extends Component {
  state = {
    phone:"",
    mail:"",
    userCode:"",
    mailCode:"",
    checked1: true,
    checked2: false,
    size:20,
  }
  sendRequest() {
    let s = this.state;
    let url = 'http://3.25.192.210:8080/aicare-smtp/codematch?'
    +'input-code='+ s.userCode
    +'&code=' + s.mailCode;
    fetch(url,{
      method: 'POST',
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      }
    })
    .then((response) => console.log(response.status))
  //  .then(json => {console.log(json)});
  }
  sendCode() {
    let m = this.state.mail;
    let url = 'http://3.25.192.210:8080/aicare-smtp/smtp?'
    +'&mail-address=' + m
    console.log(url);
    fetch(url,{
      headers: {
      'Accept':       'application/json',
      'Content-Type': 'application/json',
      }
    })
    .then((response) => response.json())
    .then((json) => {
      this.setState({ mailCode: json.code});
      console.log(json.code);
    });
  }

  render() {
    return (
      <SafeAreaView style={{ flex:1, justifyContent: "center", alignItems: "center" }}>
          <View style={{alignItems: "Left" }}>
          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/singup_icon_name.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>绑定方式</Text>
          </View>

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <CheckBox
              center
              title='电话'
              iconRight
              checkedIcon='check-circle-o'
              uncheckedIcon='circle-o'
              checkedColor='red'
              size={this.state.size}
              checked={this.state.checked1}
              onPress={() => this.setState({
                checked1: true,
                checked2: false,
              })}
             />
          </View>

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <CheckBox
              center
              title='邮箱'
              iconRight
              checkedIcon='check-circle-o'
              uncheckedIcon='circle-o'
              checkedColor='red'
              size={this.state.size}
              checked={this.state.checked2}
              onPress={() => this.setState({
                checked1: false,
                checked2: true,
              })}
             />
          </View>
          <TextInput style={styles.resumeInput}
          placeholder="请输入您的邮箱"
          onChangeText={(text) => {this.setState({ mail: text})}}
          />

          <View style={{marginTop:15, marginBottom:15,flexDirection: 'row'}}>
            <Image
              style = {styles.smallIconImg}
              source={require('../images/providerImg/signup_icon_key.png')}
            />
            <Text style={{ fontSize:18, fontWeight: '500' }}>验证码</Text>
            <TouchableOpacity style={{width:'auto', marginTop: 3, marginLeft: 25, paddingLeft:10, paddingRight:10, height:20,borderRadius:10,borderWidth:1}}
              onPress={()=>this.sendCode()}
            >
              <Text style={{ fontSize:14, fontWeight: '300' }}>获取验证码</Text>
            </TouchableOpacity>
          </View>
          <TextInput style={styles.resumeInput}
          placeholder= "请输入您邮箱内收到的验证码"
          onChangeText={(text) => {this.setState({ userCode: text})}}
          />
          </View>
          <TouchableOpacity style={styles.resumeButton} onPress={()=>this.sendRequest()}>
            <Text style={{ fontSize:16, fontWeight: '400', color: '#ffffff' }}>下一步</Text>
          </TouchableOpacity>
      </SafeAreaView>
  );}
}
